package day2examples;

public class StringConcatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String ab=new String();
		ab="Welcome";
				
				String s1="Automation";
				String s2="team";
				String s3=s1+s2;
				int w =20;
				int d=25;
				System.out.println(ab);
				System.out.println(s1+                         s2);
				System.out.println(s3);
				System.out.println(w+d+s1+s2);
	
			System.out.println("before"+w+d);

			System.out.println(w+d+"after the integer");
		
		//Extra spaces should be given in the value initialization field and not in the print line
			
		
	}

}
